package com.controlflow;

public class Month {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int month = 5;

		switch (month) {
		case 1:
			System.out.println("jan");
			break;
		case 2:
			System.out.println("feb");
			break;
		case 3:
			System.out.println("march");
			break;
			
			default:
				System.out.println("invalid");

		}
	}

}
